package com.bcj.corejava.flowcontrol.lab4;

/**
 * @author Bootcamp User 005
 *
 */
public class LoopPrintTable {

	/**
	 * main method prints the table  acccording to the requirement  by using the for loop
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("N\t N*10\t N*100\t N*1000");
		for(int i=1;i<=5;i++){
			System.out.println(i+"\t "+i*10+"\t "+i*100+"\t "+i*1000);
			
		}

	}

}
